import React, { useContext, useState, useEffect } from "react";
import API from "../api/axios";
import AuthContext from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2"; // Import Swal

const partyLogos = {
  BJP: "data:image/jpeg;base64,",
  INC: "data:image/png;base64,",
  AAP: "data:image/jpeg;base64,",
  BSP: "data:image/png;base64,",
};

const Voting = () => {
  const { user } = useContext(AuthContext);
  const [party, setParty] = useState("");
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [hasVoted, setHasVoted] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const votedStatus = localStorage.getItem(`${user.username}_hasVoted`);
    if (votedStatus === "true") {
      setHasVoted(true);
    }
  }, [user.username]);

  const handleVote = async () => {
    try {
      await API.post(
        "/vote",
        { party, voter: user.username },
        { headers: { Authorization: `Bearer ${user.token}` } }
      );

      localStorage.setItem(`${user.username}_hasVoted`, "true");
      setHasVoted(true);

      Swal.fire({
        title: "Vote Cast Successfully!",
        text: "Your vote has been successfully recorded.",
        icon: "success",
        confirmButtonText: "Okay",
      });

      navigate("/results");
    } catch (err) {
      console.error(err.response.data.message);

      Swal.fire({
        title: "Failed to Cast Vote!",
        text: "There was an issue with your vote submission. Please try again.",
        icon: "error",
        confirmButtonText: "Try Again",
      });
    }
  };

  return (
    <div className="bg-gradient-to-r from-indigo-500 to-blue-600 min-h-screen flex items-center justify-center">
      <div className="w-full max-w-lg bg-white p-8 rounded-lg shadow-lg">
        <h1 className="text-3xl font-semibold text-center text-gray-800 mb-6">
          Cast Your Vote
        </h1>

        <div className="mb-4 relative">
          <label
            htmlFor="party"
            className="block text-gray-700 text-lg mb-2"
          >
            Select Party
          </label>

          <div className="relative">
            <button
              onClick={() => setIsDropdownOpen(!isDropdownOpen)}
              className="w-full p-3 border border-gray-300 rounded-lg text-left focus:outline-none focus:ring-2 focus:ring-indigo-500"
            >
              {party ? (
                <div className="flex items-center">
                  <img
                    src={partyLogos[party]}
                    alt={party}
                    className="w-6 h-6 mr-2"
                  />
                  <span>{party}</span>
                </div>
              ) : (
                "Select Party"
              )}
            </button>

            {isDropdownOpen && (
              <ul className="absolute left-0 w-full mt-1 bg-white border border-gray-300 rounded-lg shadow-lg max-h-60 overflow-y-auto">
                {Object.keys(partyLogos).map((partyName) => (
                  <li
                    key={partyName}
                    onClick={() => {
                      setParty(partyName);
                      setIsDropdownOpen(false);
                    }}
                    className="flex items-center p-3 hover:bg-gray-100 cursor-pointer"
                  >
                    <img
                      src={partyLogos[partyName]}
                      alt={partyName}
                      className="w-6 h-6 mr-2"
                    />
                    <span>{partyName}</span>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>

        <button
          onClick={handleVote}
          className="w-full bg-green-500 hover:bg-green-600 text-white font-semibold py-3 rounded-lg focus:outline-none focus:ring-4 focus:ring-green-300"
          disabled={hasVoted}
        >
          {hasVoted ? "You Have Voted" : "Vote"}
        </button>

        {/* Fixed Return Home Button */}
        <button
          onClick={() => navigate("/")}
          className="w-full mt-4 bg-blue-500 hover:bg-blue-600 text-white font-semibold py-3 rounded-lg focus:outline-none focus:ring-4 focus:ring-blue-300"
        >
          Return Home
        </button>

        <div className="mt-6 text-center">
          <p className="text-gray-600">
            You are voting as:{" "}
            <span className="font-semibold text-gray-800">
              {user.username}
            </span>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Voting;
